import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header.js';

const ArtisanRandom = () => {
  const [artisan, setArtisan] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchRandomArtisan = () => {
    setLoading(true);
    fetch('http://localhost:3001/api/artisan/random', {
      headers: {
        'Accept': 'application/json'
      },
      mode: 'cors'
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Erreur lors de la récupération de l\'artisan');
        }
        return response.json();
      })
      .then(data => {
        console.log('Artisan reçu:', data);
        setArtisan(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Erreur:', err);
        setError(err.message);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchRandomArtisan();
  }, []);

  if (loading) return <div>Chargement...</div>;
  if (error) return <div>Erreur : {error}</div>;
  if (!artisan) return <div>Aucun artisan trouvé</div>;

  return (
    <>
      <Header />
      <main className="artisan-random-container">
        <h1>Artisan Aléatoire</h1>
        <button onClick={fetchRandomArtisan} className="refresh-button">
          Voir un autre artisan
        </button>
        <div className="artisan-card">
          <img
            src={`${process.env.PUBLIC_URL}/img/${artisan.image}`}
            alt={`Photo de ${artisan.nom}`}
            onError={(e) => {
              e.target.src = `${process.env.PUBLIC_URL}/img/img-default.jpg`;
            }}
          />
          <div className="artisan-info">
            <h2>{artisan.nom}</h2>
            <p className="profession">{artisan.profession}</p>
            <p className="specialite">{artisan.specialite}</p>
            <p className="localisation">{artisan.localisation}</p>
            <Link to={`/artisan/${artisan.id}`} className="voir-plus">
              Voir plus
            </Link>
          </div>
        </div>
      </main>
    </>
  );
};

export default ArtisanRandom;
